mapper.select:
select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select s.* ,c.name categoryName from setmeal as s left join category c on s.category_id = c.id
and s.name like concat('%',#{name},'%')
and category_id = #{categoryId}
and s.status = #{status}
order by s.create_time desc
